import React, { Component } from 'react';
import axios from 'axios';

class All extends Component {

	constructor(props){
		super(props)

		this.state = {
			movies: []
		}
	}


	componentDidMount(){
		var proxy = 'https://cors-anywhere.herokuapp.com/',
		apiLink = 'https://api.themoviedb.org/3/movie/popular?api_key=36ccdeb1a698391b27bb03f49ececac1&language=en-US&page=1'
		axios.get(proxy+apiLink)
		.then(res => res.json())
			// const movie = res.data.slice(0,10);
			.then(json => {
			this.setState({ 
				movies:json.results,
        totalResults:json.total_results,
			 });
			})
	}


	render (){
		var { movies } = this.state;  
		return (
			<div className='container'>
				<h1>
					All Movies
				</h1>
				<p>Here you can find all movie you want to show </p>
				<div className="row">
					{movies.map (movie =>
					<div className="col-6 col-lg-4">
						<div className='text-center'>
						<img src={"https://image.tmdb.org/t/p/w500/" + movie.poster_path} alt=""/>
							<h2>{movie.title}</h2>
							<p>{movie.overview}</p>
							<span>{movie.vote_average}</span>
							<p><a className="btn btn-primary border-shadow-none" href="#" role="button">View details Â»</a></p>
						</div>
					</div>
					)}
				</div>
			</div>
		);
	}
}

export default All;
