import React, { Component } from 'react';
import All from './All';

class Tabs extends Component {


	constructor(props){
		super(props)
		this.state = {
			moviesCat:'poplar'

		}
	}

	changeCategory=(chooseCat)=> {
		this.setState({moviesCat : chooseCat})
	}

	render (){
		return (
			<div className='container'>
				<ul className="nav justify-content-center" id="movie-tab" role="tablist">
					<li class="nav-item">
						<a className="nav-link active" onClick = {()=>this.changeCategory("popular") } id="tab-popular" data-toggle="pill" href="#tab-popular" role="tab" aria-controls="tab-popular" aria-selected="true" >Popular</a>
					</li>
					<li className="nav-item">
						<a className="nav-link" onClick = {()=>this.changeCategory("top_rated")} id="tab-top" data-toggle="pill" href="#tab-top" role="tab" aria-controls="tab-top" aria-selected="false" >Top Rated</a>
					</li>
					<li className="nav-item">
						<a className="nav-link" onClick = {()=>this.changeCategory("upcoming")} id="tab-soon" data-toggle="pill" href="#=tab-soon" role="tab" aria-controls="tab-soon" aria-selected="false" >Link</a>
					</li>
				</ul>
				<div className="tab-content" id="tabContent">
          <div className="tab-pane fade show active" id="tab-popular" role="tabpanel" aria-labelledby="tab-popular">
            <All moviesCat={this.state.moviesCat} />
          </div>
        </div>
			</div>

		);
	}
}

export default Tabs;
