import React, { Component } from 'react';
import axios from 'axios';

const $ = window.$;

class All extends Component {

	constructor(props){
		super(props)
		this.state = {
			movies: [],
			movieCategory:"popular",
			currentPage:1,
			todosPerPage: 3,
			upperPageBound: 3,
      lowerPageBound: 0,
      isPrevBtnActive: 'disabled',
      isNextBtnActive: '',
      pageBound: 3
		};
		this.handleClick = this.handleClick.bind(this);
		this.btnDecrementClick = this.btnDecrementClick.bind(this);
		this.btnIncrementClick = this.btnIncrementClick.bind(this);
		this.btnNextClick = this.btnNextClick.bind(this);
		this.btnPrevClick = this.btnPrevClick.bind(this);
		this.setPrevAndNextBtnClass = this.setPrevAndNextBtnClass.bind(this);

	}

	componentDidUpdate() {
		$("ul li.active").removeClass('active');
		$('ul li#'+this.state.currentPage).addClass('active');
	}

	handleClick(event) {
		let listid = Number(event.target.id);
		this.setState({
			currentPage: listid
		});
		$("ul li.active").removeClass('active');
		$('ul li#'+listid).addClass('active');
		this.setPrevAndNextBtnClass(listid);
	}
	setPrevAndNextBtnClass(listid) {
		let totalPage = Math.ceil(this.state.movies.length / this.state.todosPerPage);
		this.setState({isNextBtnActive: 'disabled'});
		this.setState({isPrevBtnActive: 'disabled'});
		if(totalPage === listid && totalPage > 1){
				this.setState({isPrevBtnActive: ''});
		}
		else if(listid === 1 && totalPage > 1){
				this.setState({isNextBtnActive: ''});
		}
		else if(totalPage > 1){
				this.setState({isNextBtnActive: ''});
				this.setState({isPrevBtnActive: ''});
		}
	}

	btnIncrementClick() {
		this.setState({upperPageBound: this.state.upperPageBound + this.state.pageBound});
		this.setState({lowerPageBound: this.state.lowerPageBound + this.state.pageBound});
		let listid = this.state.upperPageBound + 1;
		this.setState({ currentPage: listid});
		this.setPrevAndNextBtnClass(listid);
	}
	btnDecrementClick() {
		this.setState({upperPageBound: this.state.upperPageBound - this.state.pageBound});
		this.setState({lowerPageBound: this.state.lowerPageBound - this.state.pageBound});
		let listid = this.state.upperPageBound - this.state.pageBound;
		this.setState({ currentPage: listid});
		this.setPrevAndNextBtnClass(listid);
	}
	btnPrevClick() {
		if((this.state.currentPage -1)%this.state.pageBound === 0 ){
				this.setState({upperPageBound: this.state.upperPageBound - this.state.pageBound});
				this.setState({lowerPageBound: this.state.lowerPageBound - this.state.pageBound});
		}
		let listid = this.state.currentPage - 1;
		this.setState({ currentPage : listid});
		this.setPrevAndNextBtnClass(listid);
	}
	btnNextClick() {
			if((this.state.currentPage +1) > this.state.upperPageBound ){
					this.setState({upperPageBound: this.state.upperPageBound + this.state.pageBound});
					this.setState({lowerPageBound: this.state.lowerPageBound + this.state.pageBound});
			}
			let listid = this.state.currentPage + 1;
			this.setState({ currentPage : listid});
			this.setPrevAndNextBtnClass(listid);
	}

	componentDidMount(){
		this.callingAPI();
	}
	callingAPI(){
		var proxy = 'https://cors-anywhere.herokuapp.com/',
		apiLink = `https://api.themoviedb.org/3/movie/${this.state.movieCategory}?api_key=36ccdeb1a698391b27bb03f49ececac1&language=en-US&page=${this.state.currentPage}`
		fetch(proxy+apiLink)
		.then(res => res.json())
			// const movie = res.data.slice(0,10);
			.then(json => {
			this.setState({ 
				movies:json.results,
        totalResults:json.total_results,
			 });
		})
	}



	render (){
		var { movies } = this.state;  
		const { currentPage, todosPerPage,upperPageBound,lowerPageBound,isPrevBtnActive,isNextBtnActive } = this.state;
		// Logic for displaying current todos
		const indexOfLastTodo = currentPage * todosPerPage;
		const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
		const currentTodos = movies.slice(indexOfFirstTodo, indexOfLastTodo);
		const pageNumbers = [];
        for (let i = 1; i <= Math.ceil(movies.length / todosPerPage); i++) {
          pageNumbers.push(i);
        }

        const renderPageNumbers = pageNumbers.map(number => {
            if(number === 1 && currentPage === 1){
                return(
                    <li key={number} className='active' id={number}><a href='#' id={number} onClick={this.handleClick}>{number}</a></li>
                )
            }
            else if((number < upperPageBound + 1) && number > lowerPageBound){
                return(
                    <li key={number} id={number}><a href='#' id={number} onClick={this.handleClick}>{number}</a></li>
                )
            }
        });
        let pageIncrementBtn = null;
        if(pageNumbers.length > upperPageBound){
            pageIncrementBtn = <li className=''><a href='#' onClick={this.btnIncrementClick}> &hellip; </a></li>
        }
        let pageDecrementBtn = null;
        if(lowerPageBound >= 1){
            pageDecrementBtn = <li className=''><a href='#' onClick={this.btnDecrementClick}> &hellip; </a></li>
        }
        let renderPrevBtn = null;
        if(isPrevBtnActive === 'disabled') {
            renderPrevBtn = <li className={isPrevBtnActive}><span id="btnPrev"> Prev </span></li>
        }
        else{
            renderPrevBtn = <li className={isPrevBtnActive}><a href='#' id="btnPrev" onClick={this.btnPrevClick}> Prev </a></li>
        }
        let renderNextBtn = null;
        if(isNextBtnActive === 'disabled') {
            renderNextBtn = <li className={isNextBtnActive}><span id="btnNext"> Next </span></li>
        }
        else{
            renderNextBtn = <li className={isNextBtnActive}><a href='#' id="btnNext" onClick={this.btnNextClick}> Next </a></li>
        }
		return (
			<div className='container'>
				<div className='thumbnails'>
					<h1>
						All Movies
					</h1>
					<p>Here you can find all movie you want to show </p>
					<div className="row">
						{movies.map (movie =>
						<div className="col-6 col-md-3">
							<img src={"https://image.tmdb.org/t/p/w500/" + movie.poster_path} alt=""/>
							<h2>{movie.title}</h2>
							<p>{movie.overview}</p>
							<span>
								<span className='rate-icon'></span>
								<span className='rate-value'>{movie.vote_average}</span>
							</span>
							<p><a className="btn btn-primary border-shadow-none" href="#" role="button">View details Â»</a></p>
						</div>
						)}
					</div>
				</div>
				<ul className="pagination">
              {renderPrevBtn}
              {pageDecrementBtn}
              {renderPageNumbers}
              {pageIncrementBtn}
              {renderNextBtn}
            </ul>
			</div>
		);
	}
}

export default All;
