import React, { Component } from 'react';
import axios from 'axios';

class All extends Component {

	constructor(props){
		super(props)

		this.state = {
			movie: []
		}
	}


	componentDidMount(){
		axios.get('https://www.themoviedb.org/discover/movie?primary_release_date.gte=2014-09-15&primary_release_date.lte=2014-10-22')
		.then(res =>{
			const movie = res.data.slice(0,10);
			this.setState({ movie });
		})
	}


	render (){
		return (
			<div className='container'>
				<h1>
					All Movies
				</h1>
				<p>Here you can find all movie you want to show </p>
				<div className="row">
					{this.state.movie.map (movie =>
					<div class="col-6 col-lg-4">
						<div className='text-center'>
							<svg class="bd-placeholder-img rounded-circle" width="140" height="140" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" focusable="false" role="img" aria-label="Placeholder: 140x140"><title>Placeholder</title><rect width="100%" height="100%" fill="#777"></rect><text x="50%" y="50%" fill="#777" dy=".3em">140x140</text></svg>
							<h2>{movie.title}</h2>
							<p>{movie.description}</p>
							<span>{movie.rate}</span>
							<p><a class="btn btn-primary border-shadow-none" href="#" role="button">View details Â»</a></p>
						</div>
					</div>
					)}
				</div>
			</div>
		);
	}
}

export default All;
